/**
 * Properties for <flutter-shadcn-separator>
 *
 * A visual separator/divider component.
 *
 * @example
 * ```html
 * <flutter-shadcn-separator orientation="horizontal" />
 * ```
 */
interface FlutterShadcnSeparatorProperties {
  /**
   * Orientation of the separator.
   * Options: 'horizontal', 'vertical'
   * Default: 'horizontal'
   */
  orientation?: string;
}

interface FlutterShadcnSeparatorEvents {}
